import React, { Component } from "react";
import ProductGrid from "./productGrid";
import SidePanel from "./sidePanel";
import axios from "axios";
import moment from "moment";

class Display extends Component {
  state = {
    products: [],
    productsCount: 12,
    skipCount: 0,
    filters: [],
    brandName: "",
    discountRange: [0, 100],
    inStock: false,
    startDate: null,
    endDate: null,
  };

  handleRangeChange = (value) => {
    this.setState({ discountRange: value });
  };

  handleSwitchChange = () => {
    this.setState({ inStock: !this.state.inStock });
  };

  handleStartDateChange = (date) => {
    this.setState({
      startDate: date,
    });
  };

  handleEndDateChange = (date) => {
    this.setState({
      endDate: date,
    });
  };

  handleBrandChange = (e) => {
    this.setState({ brandName: e.target.value });
  };

  handleFilter = async () => {
    const {
      brandName,
      startDate,
      endDate,
      discountRange,
      inStock,
    } = this.state;

    let filters = [];
    filters.push({
      key: "similar_products.meta.avg_discount",
      value: [discountRange[0] / 100, discountRange[1] / 100],
      operator: "between",
    });
    if (brandName !== "") {
      filters.push({
        key: "brand.name",
        value: brandName,
        operator: "contains",
      });
    }
    if (inStock === true) {
      filters.push({
        key: "stock.available",
        value: inStock,
        operator: "exact",
      });
    }
    if (startDate !== "" && endDate !== "") {
      filters.push({
        key: "created_at",
        value: [startDate, endDate],
        operator: "between",
      });
    }
    console.log(filters);
  };

  async componentDidMount() {}

  render() {
    const {
      brandName,
      startDate,
      endDate,
      discountRange,
      inStock,
    } = this.state;

    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-2">
            <SidePanel
              onRangeChange={this.handleRangeChange}
              onSwitchChange={this.handleSwitchChange}
              onStartDateChange={this.handleStartDateChange}
              onEndDateChange={this.handleEndDateChange}
              onBrandChange={this.handleBrandChange}
              onClick={this.handleFilter}
              dualValue={discountRange}
              isSwitchChecked={inStock}
              startDate={startDate}
              endDate={endDate}
              brandName={brandName}
            />
          </div>
          <div className="col">
            <ProductGrid />
          </div>
        </div>
      </div>
    );
  }
}

export default Display;
