import React, { Component } from "react";
import ProductGrid from "./productGrid";
import SidePanel from "./sidePanel";
import axios from "axios";
import moment from "moment";

class Display extends Component {
  state = {
    products: [],
    productsCount: 12,
    skipCount: 0,
    filters: [],
    brandName: "",
    discountRange: [0, 100],
    inStock: false,
    startDate: "",
    endDate: "",
  };

  handleRangeChange = (value) => {
    this.setState({ discountRange: value });
  };

  handleSwitchChange = () => {
    this.setState({ isSwitchChecked: !this.state.isSwitchChecked });
  };

  handleStartDateChange = (date) => {
    this.setState({
      startDate: date,
    });
  };

  handleEndDateChange = (date) => {
    this.setState({
      endDate: date,
    });
  };

  handleFilter = async () => {
    let filters = [];
    if (this.state.brand_name !== "") {
      obj.push({
        key: "brand.name",
        value: this.props.brand_name,
        operator: "contains",
      });
    }
    if (this.props.stock === true) {
      obj.push({ key: "stock.available", value: true, operator: "exact" });
    }
    if (this.props.startdate !== "" && this.props.enddate !== "") {
      obj.push({
        key: "created_at",
        value: [this.props.startdate, this.props.enddate],
        operator: "between",
      });
    }
  };
  async componentDidMount() {}
  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-2">
            <SidePanel
              onRangeChange={this.handleRangeChange}
              onSwitchChange={this.handleSwitchChange}
              onStartDateChange={this.handleStartDateChange}
              onEndDateChange={this.handleEndDateChange}
              onClick={this.handleFilter}
              dualValue={this.state.discountRange}
              isSwitchChecked={this.state.inStock}
              startDate={this.state.startDate}
              endDate={this.state.endDate}
              brandName={this.state.brandName}
            />
          </div>
          <div className="col">
            <ProductGrid />
          </div>
        </div>
      </div>
    );
  }
}

export default Display;
