import React, { Component } from "react";
import ProductGrid from "./productGrid";
import SidePanel from "./sidePanel";
import axios from "axios";
import moment from "moment";

class Display extends Component {
  state = {
    products: [],
    productsCount: 12,
    skipCount: 0,
    filters: [],
    brandName: "",
    discountRange: [0, 100],
    inStock: false,
    startDate: null,
    endDate: null,
  };

  handleRangeChange = (value) => {
    this.setState({ discountRange: value });
  };

  handleSwitchChange = () => {
    this.setState({ inStock: !this.state.inStock });
  };

  handleStartDateChange = (date) => {
    this.setState({
      startDate: date,
    });
  };

  handleEndDateChange = (date) => {
    this.setState({
      endDate: Date,
    });
  };

  handleBrandChange = (e) => {
    this.setState({ brandName: e.target.value });
  };

  handleFilter = async () => {
    let filters = [];
    filters.push({
      key: "similar_products.meta.avg_discount",
      value: [
        this.state.discountRange[0] / 100,
        this.state.discountRange[1] / 100,
      ],
      operator: "between",
    });
    if (this.state.brandName !== "") {
      filters.push({
        key: "brand.name",
        value: this.state.brandName,
        operator: "contains",
      });
    }
    if (this.state.inStock === true) {
      filters.push({
        key: "stock.available",
        value: this.state.inStock,
        operator: "exact",
      });
    }
    if (this.state.startDate !== "" && this.state.endDate !== "") {
      filters.push({
        key: "created_at",
        value: [this.state.startDate, this.state.endDate],
        operator: "between",
      });
    }
    console.log(filters);
  };

  async componentDidMount() {}

  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-2">
            <SidePanel
              onRangeChange={this.handleRangeChange}
              onSwitchChange={this.handleSwitchChange}
              onStartDateChange={this.handleStartDateChange}
              onEndDateChange={this.handleEndDateChange}
              onBrandChange={this.handleBrandChange}
              onClick={this.handleFilter}
              dualValue={this.state.discountRange}
              isSwitchChecked={this.state.inStock}
              startDate={this.state.startDate}
              endDate={this.state.endDate}
              brandName={this.state.brandName}
            />
          </div>
          <div className="col">
            <ProductGrid />
          </div>
        </div>
      </div>
    );
  }
}

export default Display;
